<template>
	<DashboardWidget :items="tasks" :loading="loading" :show-more-url="''">
		<template #default="{ item }">
			<DashboardWidgetItem
				:target-url="getItemUrl(item.id)"
				:avatar-url="getAvatarUrl()"
				:avatar-username="getAvatarUsername()"
				:avatar-is-no-user="true"
				:main-text="'Task #' + parseTargetMtype(item) + ' - ' + item.files_total + ' file(s) (' + formatBytes(item.files_total_size) + ')'"
				:sub-text="`${item.owner} - ${getStatusBadge(item)}`" />
		</template>
		<template #empty-content>
			<div class="empty-tasks-list">
				<img class="empty-tasks-list-icon" src="data:image/svg+xml;base64,">
				<img class="empty-tasks-list-icon--dark" src="data:image/svg+xml;base64,">
				<strong>{{ t('mediadc', 'No tasks yet.') }}</strong>
				<a :href="getAppUrl()">{{ t('mediadc', 'Create a new one!') }}</a>
			</div>
		</template>
	</DashboardWidget>
</template>

<script>
import { generateUrl } from '@nextcloud/router'
import { DashboardWidget, DashboardWidgetItem } from '@nextcloud/vue-dashboard'
import { loadState } from '@nextcloud/initial-state'
import Formats from '../mixins/Formats'
import { getCurrentUser } from '@nextcloud/auth'

const tasks = loadState('mediadc', 'mediadc-recent-tasks')

export default {
	name: 'Dashboard',
	components: {
		DashboardWidget,
		DashboardWidgetItem,
	},
	mixins: [
		Formats,
	],
	data() {
		return {
			tasks,
			loading: true,
		}
	},
	async mounted() {
		this.loading = false
	},
	methods: {
		getAppUrl() {
			return generateUrl('/apps/mediadc')
		},
		getItemUrl(taskId) {
			return generateUrl(`/apps/mediadc/tasks/${taskId}`)
		},
		getAvatarUrl() {
			return generateUrl(`/avatar/${getCurrentUser().uid}/32`)
		},
		getAvatarUsername() {
			return getCurrentUser().uid
		},
	},
}
</script>

<style scoped>
h1 {
	padding: 20px;
	text-align: center;
}

ul {
	display: block;
	height: 100%;
	overflow-y: scroll;
}

.empty-tasks-list {
	padding: 20px;
	width: 100%;
	height: 80%;
	display: flex;
	align-items: center;
	justify-content: center;
	box-sizing: border-box;
	flex-direction: column;
}

.empty-tasks-list strong {
	margin: 10px 0 0;
}

.empty-tasks-list a {
	text-decoration: underline;
}

.empty-tasks-list-icon, .empty-tasks-list-icon--dark {
	width: 96px;
	height: 96px;
}

.empty-tasks-list-icon--dark {
	display: none;
	visibility: hidden;
}

body.theme--dark .empty-tasks-list-icon--dark {
	display: block;
	visibility: visible;
	opacity: 0.5;
}

body.theme--dark .empty-tasks-list-icon {
	display: none;
	visibility: hidden;
}
</style>
